namespace Zephir\Commands;

/**
 * Zephir\Commands\CommandFullClean
 *
 * Cleans any object files created by the extension (including files generated by phpize)
 *
 * @package Zephir\Commands
 */
class CommandFullClean extends CommandAbstract
{
    /**
     * {@inheritdoc}
     *
     * @return string
     */
    public function getCommand()
    {
        return 'fullclean';
    }

    /**
     * {@inheritdoc}
     *
     * @return string
     */
    public function getUsage()
    {
        return $this->getCommand();
    }

    /**
     * {@inheritdoc}
     *
     * @return string
     */
    public function getDescription()
    {
        return 'Cleans any object files created by the extension (including files generated by phpize)';
    }
}
